import os, sys
import json

import boto3
from boto3.dynamodb.conditions import Key, Attr

from harvest.dynamodb_driver import DynamoDbDriver


TABLE_NAEME = "projects"

class ProjectDriver():

  def __init__(self, host, port):
    self.dynamodb = DynamoDbDriver(host, port, "projects")
  
  def get_all(self):
    return self.dynamodb.scan()


