from datetime import datetime
from calendar import monthrange

PRICE_PER_ACCOUNT = 500

class Billing():

  def __init__(self):
    pass

  def get_price(self, account_cnt, month=None):
    return account_cnt * PRICE_PER_ACCOUNT

  #TODO: 月指定での請求日を求められる機能があったほうが良いかも
  def get_cycle(self):
    now = datetime.now()
    last_day = monthrange(now.year, now.month)[1]
    return now.replace(day=last_day, hour=0, minute=0, second=0, microsecond=0).isoformat()
